mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY BUCKET_SEQ DESC) AS RN, BUCKET_SEQ, USER_SEQ, BUCKET_NM, BUCKET_DSCR, REP_IMG_URL, REG_DTTM, DEL_YN, (CASE WHEN Count(*) OVER() > ( #{stNo} + #{searchCnt} ) THEN 'Y' ELSE 'N' END) AS MORE_YN FROM TB_BUCKET_MNG WHERE DEL_YN = 'N'
AND USER_SEQ = #{userSeq}
ORDER BY BUCKET_SEQ DESC LIMIT #{stNo}, #{searchCnt}

mapper.update:
/*[ SQL ID : BucketMapper.delStory]*/
	UPDATE TB_BUCKET_MNG
	   SET DEL_YN = 'Y'
	 WHERE BUCKET_SEQNO = #{bucketSeqno}
